// Test environment should use vendasta-internal certs
// Demo environmnet should use vendasta-internal or vendasta.com certs
// Prod environmnet should use vendasta-internal or vendasta.com certs

type tlsPems struct {
	ServerCertPem []byte
	ServerKeyPem  []byte
	CaCertPem     []byte
}

func writePem(fileName, pemType string, derBytes []byte) {
	var err error
	var f *os.File
	if f, err = os.Create(fileName); err != nil {
		log.Fatalf("Error creating ca cert file: %s", err.Error())
	}
	pem.Encode(f, &pem.Block{Type: pemType, Bytes: derBytes})
	f.Close()
}

func generateLocalCerts() *tlsPems {
	var err error
	var caPriv, priv *rsa.PrivateKey

	//Generate two RSA keypairs
	if caPriv, err = rsa.GenerateKey(rand.Reader, 2048); err != nil {
		log.Fatalf("Error generating CA RSA key: %s", err.Error())
	}
	if priv, err = rsa.GenerateKey(rand.Reader, 2048); err != nil {
		log.Fatalf("Error generating RSA key: %s", err.Error())
	}

	//Generate the CA cert
	template := x509.Certificate{
		SerialNumber:          big.NewInt(1),
		Subject:               pkix.Name{Organization: []string{"Vendasta Local Co"}},
		NotBefore:             time.Now(),
		DNSNames:              []string{"localhost", "minikube.xxx"},
		NotAfter:              time.Now().AddDate(5, 0, 0),
		KeyUsage:              x509.KeyUsageKeyEncipherment | x509.KeyUsageDigitalSignature | x509.KeyUsageCertSign,
		ExtKeyUsage:           []x509.ExtKeyUsage{x509.ExtKeyUsageServerAuth},
		BasicConstraintsValid: true,
	}
	var derBytes []byte
	fmt.Printf("Generating for public key: %#v\n", caPriv.PublicKey)
	if derBytes, err = x509.CreateCertificate(rand.Reader, &template, &template, &caPriv.PublicKey, caPriv); err != nil {
		log.Fatalf("Error generating CA certificate: %s", err.Error())
	}
	rvalue := &tlsPems{}
	rvalue.CaCertPem = pem.EncodeToMemory(&pem.Block{Type: "CERTIFICATE", Bytes: derBytes})

	//Generate the server cert
	template = x509.Certificate{
		SerialNumber:          big.NewInt(2),
		Subject:               pkix.Name{Organization: []string{"Vendasta Local Co"}},
		NotBefore:             time.Now(),
		DNSNames:              []string{"localhost", "minikube.xxx"},
		NotAfter:              time.Now().AddDate(5, 0, 0),
		KeyUsage:              x509.KeyUsageKeyEncipherment | x509.KeyUsageDigitalSignature,
		ExtKeyUsage:           []x509.ExtKeyUsage{x509.ExtKeyUsageServerAuth},
		BasicConstraintsValid: true,
	}
	if derBytes, err = x509.CreateCertificate(rand.Reader, &template, &template, &priv.PublicKey, caPriv); err != nil {
		log.Fatalf("Error generating Server certificate: %s", err.Error())
	}
	rvalue.ServerCertPem = pem.EncodeToMemory(&pem.Block{Type: "CERTIFICATE", Bytes: derBytes})

	//Generate the server key
	derBytes = x509.MarshalPKCS1PrivateKey(priv)
	rvalue.ServerKeyPem = pem.EncodeToMemory(&pem.Block{Type: "RSA PRIVATE KEY", Bytes: derBytes})

	return rvalue
}

