package templates

//EnumsIndexTemplate is the template for generated enums index.ts
var EnumsIndexTemplate = `{{- $descriptors := . -}}
// *********************************
// Code generated by sdkgen
// DO NOT EDIT!.
//
// Enums Index.
// *********************************
{{ range $descriptor := $descriptors }}
{{- if gt (len $descriptor.Enums) 0 -}}
export {
	{{- range $enum := $descriptor.Enums }}
	{{ $enum.Name }},
	{{- end }}
} from './{{ $descriptor.TsEnumFileName }}';
{{- end -}}
{{ end -}}

`
