package templates

//IndexTemplate is the template for generating the _internal directories index.ts
var IndexTemplate = `{{- $template := . -}}
// *********************************
// Code generated by sdkgen
// DO NOT EDIT!.
//
// Index.
// *********************************
{{ if eq $template.IncludeEnums true }}
export * from './enums';
{{ end -}}
export * from './objects';
export * from './interfaces';
{{- range $template.Proto.Services -}}{{- $service := . }}
export { {{ $service.TsName }}ApiService } from './{{ $service.TsFileName }}.api.service';
{{- end }}
`
