package templates

//InterfacesTemplate is the template for generating interfaces for the object layer
var InterfacesTemplate = `{{- $template := . -}}
// *********************************
// Code generated by sdkgen
// DO NOT EDIT!.
//
// Interfaces.
// *********************************
import * as i from '.';
{{ if eq $template.IncludeEnums true -}}
import * as e from '../enums';
{{ end -}}
{{ range $message := $template.Descriptor.Messages }}
export interface {{$message.InterfaceName}} {
	{{- range $field := $message.Message.Field }}
	{{ $field.JsonName }}?: {{ $template.Descriptor.FieldToProperty $field true }};
	{{- end }}
}
{{ end }}
`
